import React, { Component } from 'react';


 class List extends Component {

  constructor(props) {
    super(props);

    this.state = {
      resAPI: ""
    };
  }


  componentWillMount() {
    fetch('http://localhost:3000/list')
      .then(res => res.text())
      .then(data => this.setState({ resAPI: data }));
  }

  render() {
    return (
      <div className="contact">
        <h1>Liste</h1>
        <button  id="boutton1" onClick={()=>{

            var list = this.state.resAPI
            var lister = JSON.parse(list)
            var pushlist = []

          for(var i=0;i<lister.length;i++)
          {
            pushlist.map(lister)
            document.getElementById("name").innerHTML += "<img id='monimage' src="+lister[i].image+"/>";
            document.getElementById("name").innerHTML += "<span id='spannom'> Nom : "+lister[i].nom+"</span><br>";
            document.getElementById("name").innerHTML += "<span id='spanprenom'> Prénom : "+lister[i].Prenom+ "</span><br>";


          }
        }}>
            LISTER
        </button>

        <ul>
          {this.state.lister.map(item => (
            <li key={item}>{item}</li>
          ))}
        </ul>

          {/* <div id="name"></div> */}

      </div>
    )
  }
}

export default List;
