import React, { Component } from 'react';


 class List extends Component {

  constructor(props) {
    super(props);

    this.state = {
      resAPI: ""
    };
  }


  componentWillMount() {
    fetch('http://localhost:3000/list')
      .then(res => res.text())
      .then(data => this.setState({ resAPI: data }));
  }

  render() {
    return (
      <div className="contact">
        <h1>Liste</h1>
        <button   onClick={()=>{

            //var file = readFileSync('note.json', 'utf8')
            var list = this.state.resAPI
            var lister = JSON.parse(list)

          for(var i=0;i<lister.length;i++)
          {
            document.getElementById("image").innerHTML += "<img id='monimage' src="+lister[i].image+"/>";
            document.getElementById("nom").innerHTML += "<span id=''>".lister[i].nom + "</span> ";
            document.getElementById("prenom").innerHTML += lister[i].Prenom+ "<br>";


          }
        }}>
            LISTER
        </button>
        <div class="row">
            <div className="col-md-6">
                <div id="image"></div>
            </div>
            <div className="col-md-6">
              <p id="nom"></p>
              <p id="prenom"></p>
            </div>
        </div>
        <div id="nom"></div>
      </div>
    )
  }
}

export default List;
